export default function SkillsSection() {
  const [activeTab, setActiveTab] = useState<"proficient" | "exposed">(
    "proficient"
  );

  return (
    <section className="py-20">
      <div className="text-center mb-12">
        <h2 className="text-sm uppercase tracking-wider text-gray-400 mb-2">
          MY EXPERTISE
        </h2>
        <h3 className="text-4xl font-bold relative inline-block">
          Technical <span className="text-[#ff014f]">Skills</span>
          <span className="absolute -bottom-2 left-0 w-full h-1 bg-gradient-to-r from-transparent via-[#ff014f] to-transparent"></span>
        </h3>
      </div>

      <div className="max-w-4xl mx-auto">
        {/* Tab navigation */}
        <div className="flex justify-center mb-8">
          <div className="bg-[#191b1e] p-1 rounded-lg inline-flex">
            <button
              onClick={() => setActiveTab("proficient")}
              className={`px-6 py-3 rounded-md text-sm font-medium transition-all ${
                activeTab === "proficient"
                  ? "bg-[#ff014f] text-white"
                  : "text-gray-400 hover:text-white"
              }`}
            >
              <div className="flex items-center gap-2">
                <Code size={16} />
                <span>Proficient With</span>
              </div>
            </button>
            <button
              onClick={() => setActiveTab("exposed")}
              className={`px-6 py-3 rounded-md text-sm font-medium transition-all ${
                activeTab === "exposed"
                  ? "bg-[#ff014f] text-white"
                  : "text-gray-400 hover:text-white"
              }`}
            >
              <div className="flex items-center gap-2">
                <Lightbulb size={16} />
                <span>Exposed To</span>
              </div>
            </button>
          </div>
        </div>

        {/* Skills grid */}
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
          {activeTab === "proficient"
            ? skills.proficientWith.map((skill, index) => (
                <SkillBadge
                  key={index}
                  name={skill.name}
                  icon={skill.icon}
                  delay={index * 0.1}
                />
              ))
            : skills.exposedTo.map((skill, index) => (
                <SkillBadge
                  key={index}
                  name={skill.name}
                  icon={skill.icon}
                  delay={index * 0.1}
                />
              ))}
        </div>
      </div>
    </section>
  );
}

function SkillBadge({
  name,
  icon,
  delay,
}: {
  name: string;
  icon?: any;
  delay: number;
}) {
  return (
    <div
      className="bg-[#191b1e] rounded-lg p-4 flex items-center justify-center h-24 hover:bg-gradient-to-b hover:from-[#191b1e] hover:to-[#24272c] hover:shadow-lg transition-all duration-300 hover:-translate-y-1 group"
      style={{ animationDelay: `${delay}s` }}
    >
      <div className="text-center">
        <div className="w-12 h-12 mx-auto mb-2 bg-[#212428] rounded-full flex items-center justify-center group-hover:bg-[#ff014f] transition-colors duration-300">
          <span className="text-[#ff014f] group-hover:text-white transition-colors duration-300 text-lg font-bold">
            {icon}
          </span>
        </div>
        <span className="text-sm font-medium">{name}</span>
      </div>
    </div>
  );
}
